INSERT INTO "PGx" ("id", "rsid", "gene", "evidence", "clinical_annotation_types", "related_chemicals", "drug_response_category", "related_diseases", "annotation_text") VALUES (74, 'rs12248560', 'CYP2C19 (PA124)', '2A', 'Metabolism/PK', 'citalopram (PA449015),"escitalopram (PA10074)"', 'variant implication on drug metabolism', '', '"CC:Patients with the CC genotype may have decreased metabolism of citalopram or escitalopram as compared to patients with the TT or CT genotype. Other genetic factors, including other CYP2C19 alleles *2 rs4244285,*3 rs4986893, and clinical factors may also influence a patient''s citalopram or escitalopram metabolism. ,"CT:Patients with the CT genotype (CYP2C19*1/*17) may have an increased metabolism of citalopram or escitalopram as compared to patients with the CC genotype. Other genetic factors, including other CYP2C19 alleles *2 rs4244285,*3 rs4986893, and clinical factors may also influence a patient''s citalopram or escitalopram metabolism. ","TT:Patients with the TT genotype (CYP2C19*17/*17) may have an increased metabolism of citalopram or escitalopram as compared to patients with the CC genotype. Other genetic factors, including other CYP2C19 alleles *2 rs4244285,*3 rs4986893, and clinical factors may also influence a patient''s citalopram or escitalopram metabolism. "');
INSERT INTO "PGx" ("id", "rsid", "gene", "evidence", "clinical_annotation_types", "related_chemicals", "drug_response_category", "related_diseases", "annotation_text") VALUES (75, 'rs12248560', 'CYP2C19 (PA124)', '1A', 'Dosage,"Efficacy","Toxicity/ADR"', 'clopidogrel (PA449053)', 'Cardiovascular', 'Acute coronary syndrome (PA165108401),"Coronary Artery Disease (PA443796)","Myocardial Infarction (PA445019)"', '"CC:Patients with the CC genotype (*1/*1): 1) may have decreased activation of clopidogrel 2) may have a decreased, but not absent, risk for bleeding with clopidogrel as compared to patients with the CT or TT genotype 3) may have an increased risk for adverse cardiovascular events as compared to patients with a CT or TT genotype. Other genetic, including CYP2C19 loss-of-function alleles e.g. *2 rs4244285,*3 rs4986893, and clinical factors may also influence a patient''s risk for bleeding and adverse cardiovascular events.,"CT:Patients with the CT (*1/*17) genotype: 1) may have increased activation of clopidogrel 2) may have an increased risk of bleeding with clopidogrel as compared to patients with the CC genotype 3) may have a decreased, but not absent, risk for adverse cardiovascular events as compared to patients with a CC genotype. Current CPIC guidelines recommend no change in clopidogrel dosing based on *1/*17 genotype as of yet. Other genetic, including CYP2C19 loss-of-function alleles e.g. *2 rs4244285,*3 rs4986893, and clinical factors may also influence a patient''s risk for bleeding and adverse cardiovascular events.","TT:Patients with the TT (*17/*17) genotype: 1) may have increased activation of clopidogrel 2) may have an increased risk of bleeding with clopidogrel as compared to patients with a CC genotype 3) may have a decreased, but not absent, risk for adverse cardiovascular events as compared to patients with a CC genotype. Current CPIC guidelines recommend no change in clopidogrel dosing based on *17/*17 genotype as of yet. Other genetic, including CYP2C19 loss-of-function alleles e.g. *2 rs4244285,*3 rs4986893, and clinical factors may also influence a patient''s risk for bleeding and adverse cardiovascular events."');
INSERT INTO "PGx" ("id", "rsid", "gene", "evidence", "clinical_annotation_types", "related_chemicals", "drug_response_category", "related_diseases", "annotation_text") VALUES (76, 'rs12777823', '.', '1A', 'Dosage', 'warfarin (PA451906)', 'variant implication on Dosing', '', '"AA:Patients with the AA genotype may require a lower dose of warfarin (may require a dose reduction of 9·34 mg/week) in African Americans as compared to patients with the GG genotype. Other genetic and clinical factors may also influence a patient''s required dose of warfarin.,"AG:Patients with the AG genotype may require a lower dose of warfarin (may require a dose reduction of 6·92 mg/week) in African Americans as compared to patients with the GG genotype. Other genetic and clinical factors may also influence a patient''s required dose of warfarin.","GG:Patients with the GG genotype may require a higher dose of warfarin in African Americans as compared to patients with the AA or AG genotype. Other genetic and clinical factors may also influence a patient''s required dose of warfarin."');
INSERT INTO "PGx" ("id", "rsid", "gene", "evidence", "clinical_annotation_types", "related_chemicals", "drug_response_category", "related_diseases", "annotation_text") VALUES (233, 'rs4693075', 'COQ2 (PA142672084)', '2B', 'Toxicity/ADR', 'atorvastatin (PA448500),"hmg coa reductase inhibitors (PA133950441)","rosuvastatin (PA134308647)"', 'Drug Response effects/ toxicity Muscular Diseases', 'Muscular Diseases (PA444997)', '"CC:Patients with the CC genotype may have decreased risk of statin-related muscle symptoms as compared to patients with genotype GG or CG. Other genetic and clinical factors may also influence a patient''s risk of toxicity.,"CG:Patients with the CG genotype may have increased risk of statin-related muscle symptoms as compared to patients with genotype CC. Other genetic and clinical factors may also influence a patient''s risk of toxicity.","GG:Patients with the GG genotype may have increased risk of statin-related muscle symptoms as compared to patients with genotype CC. Other genetic and clinical factors may also influence a patient''s risk of toxicity."');
